<script>
export default {
  name: "Unicorns",

  data() {
    return {
      modalInstall: false,
      refreshing: false,
      registration: null,
      showModalRefresh: false,
      isIpad: false
    };
  },

  computed: {
    loading() {
      return this.$store.state.loading;
    },

    displayModeIsSet() {
      return (
        this.$store.getters.darkMode !== null &&
        this.$store.getters.darkMode !== undefined
      );
    },

    userIsAuthenticated() {
      return (
        this.$store.getters.user !== null &&
        this.$store.getters.user !== undefined
      );
    }
  },

  created() {
    this.$store.commit("setDarkMode", localStorage.getItem("darkMode"));

    document.addEventListener("swUpdated", this.showRefreshUI, { once: true });

    navigator.serviceWorker.addEventListener("controllerchange", () => {
      if (this.refreshing) return;
      this.refreshing = true;
      window.location.reload();
    });

    const isIos = () => {
      const userAgent = window.navigator.userAgent.toLowerCase();
      return /iphone|ipad|ipod/.test(userAgent);
    };

    const isInStandaloneMode = () =>
      "standalone" in window.navigator && window.navigator.standalone;

    if (isIos() && !isInStandaloneMode()) {
      this.modalInstall = true;
      this.isIpad = window.navigator.userAgent.toLowerCase().includes("ipad");
    }
  },

  beforeDestroy() {
    document.removeEventListener("swUpdated", this.showRefreshUI);
  },

  methods: {
    showRefreshUI(e) {
      this.registration = e.detail;
      this.showModalRefresh = true;
    },

    refreshApp() {
      this.showModalRefresh = false;
      // Protect against missing registration.waiting.
      if (!this.registration || !this.registration.waiting) {
        return;
      }
      this.registration.waiting.postMessage("skipWaiting");
    }
  }
};
</script>

